window.drawFakeQR=function(c,t){var x=c.getContext('2d');x.fillStyle='#0b1328';x.fillRect(0,0,c.width,c.height);x.fillStyle='#e2e8f0';x.font='16px system-ui';x.fillText('QR',8,20);x.font='12px system-ui';for(var i=0;i<t.length;i+=20){x.fillText(t.slice(i,i+20),8,46+(i/20)*18)}x.strokeStyle='#334155';x.lineWidth=3;x.strokeRect(6,6,40,40);x.strokeRect(c.width-46,6,40,40);x.strokeRect(6,c.height-46,40,40);}